
function adicionarLinhasValidador(f){var d=$("#addValidador").get(0);var e=d.insertRow(-1);var c=e.insertCell(0);var b=e.insertCell(1);var a=e.insertCell(2);c.innerHTML='<img src="'+f.gravatar+'" id="img-'+f.user_id+'" onclick="selecionaValidador(\''+f.user_email+"','"+f.user_complete_name+'\', this);" class="img-circle img-responsive" style="width:80px; height:80px; cursor:default; cursor:pointer;">';b.innerHTML='<font style="font-family:arial; font-size:16px; font-weight:bold; text-shadow: 1px 0px #fff;">'+f.user_complete_name+'</font><br /><font style="color: #999"><i class="fa fa-phone-square"></i>&nbsp;'+(f.telefone_celular!==null?f.telefone_celular:"-")+'<br /><i class="fa fa-phone"></i>&nbsp;'+(f.telefone_fixo!==null?f.telefone_fixo:"-")+"</font>";a.innerHTML='<font style="font-size:9px; color:#999; text-shadow: 1px 0px #fff;">Principal<br /></font><a href="mailto:'+f.user_email+'">'+f.user_email+'</a><br /><font style="font-size:9px; color:#999; text-shadow: 1px 0px #fff;">Alternativo<br /></font>'+(f.email_alternativo!==null?'<a href="mailto:'+f.email_alternativo+'">'+f.email_alternativo+"</a>":"-")}function selecionaValidador(b,a,c){$(c).attr("data-dismiss","modal");$("#validador-selecionado").html("Voc&ecirc; selecionou: <u><strong>"+a+"</strong></u> como validador desta contagem.");$("#email-validador").val(b);$("#btn-enviar-validacao").prop("disabled",false);$("#btn-selecionar-validador").prop("disabled",true);consultaGravatar(b,"#img-validador");$("#div-validador").css("visibility","visible")}function limpaValidador(){$("#validador-selecionado").html("");$("#email-validador").val("");$("#btn-enviar-validacao").prop("disabled",true);$("#btn-selecionar-validador").prop("disabled",false);$("#img-validador").attr("src","/pf/img/user.jpg");$("#div-validador").css("visibility","hidden")};