
function validar(c){var b=(c.value).replace(/\D/g,"");var a=(b).length;if(a==0){return false}else{if(!(a==11||a==14)){swal({title:"Alerta",text:"'"+b+"' Não é um CPF ou um CNPJ válido!",type:"error",html:true,confirmButtonText:"Obrigado, vou verificar!"},function(){c.value="";c.focus()});return false}}if(a==11){if(!validaCPF(b)){swal({title:"Alerta",text:"'"+b+"' Não é um CPF ou um CNPJ válido!",type:"error",html:true,confirmButtonText:"Obrigado, vou verificar!"},function(){c.value="";c.focus()});return false}return true}if(a==14){if(!validaCNPJ(b)){swal({title:"Alerta",text:"'"+b+"' Não é um CNPJ válido!",type:"error",html:true,confirmButtonText:"Obrigado, vou verificar!"},function(){c.value="";c.focus()});return false}return true}}function validaCPF(d){var f=d.substr(0,9);var b=d.substr(9,2);var e=0;for(var a=0;a<9;a++){e+=f.charAt(a)*(10-a)}if(e==0){return false}e=11-(e%11);if(e>9){e=0}if(b.charAt(0)!=e){return false}e*=2;for(var a=0;a<9;a++){e+=f.charAt(a)*(11-a)}e=11-(e%11);if(e>9){e=0}if(b.charAt(1)!=e){return false}return true}function validaCNPJ(f){var e=new Array();var d=new Number;var g=[6,5,4,3,2,9,8,7,6,5,4,3,2];for(i=0;i<12;i++){e[i]=f.charAt(i);d+=e[i]*g[i+1]}if((x=d%11)<2){e[12]=0}else{e[12]=11-x}d=0;for(y=0;y<13;y++){d+=(e[y]*g[y])}if((x=d%11)<2){e[13]=0}else{e[13]=11-x}if((f.charAt(12)!=e[12])||(f.charAt(13)!=e[13])){return false}return true};